import { useState,useEffect} from "react"
import { Link } from "react-router-dom"
import Axios from 'axios'
import "../assets/styles/style.css"
import "../assets/styles/newcar.css"
import Navbar from '../components/Navbar'





const Newcar = () => {

    const [carId,setCarId] = useState([])

            useEffect(()=>{
                var url_pre = window.location.href;
                console.log(url_pre)
                var url = url_pre.slice(30)
            
                Axios.get(`http://localhost:3001/details/${url}`).then((response)=>{
                    setCarId(response.data[0])
            
                })
            },[])

            // _______________________________________

    const [modelo,setModelo] = useState('')
    const [ano,setAno] = useState('')
    const [km,setKm] = useState('')
    const [descricao,setDescricao] = useState('')
    const [foto,setFoto] = useState('')
    const [preco,setPreco] = useState('')

    const addCar = () => {
        Axios.post("http://localhost:3001/insert",
        {modelo:modelo,ano:ano,km:km,descricao:descricao,foto:foto,preco:preco})

    }

     const updateCar = (id) => {
        Axios.put("http://localhost:3001/update",
        {id:id,modelo:modelo,ano:ano,km:km,descricao:descricao,foto:foto,preco:preco})

    }

     const Update = () => {

        

            return(
                    <>
                        <Navbar/>
                        <div class="container">
                        
                        <h1>update</h1>
                        
                            <div class="form_box">
                                

                                    
                                    <form action="http://localhost:3000/" method="put">
                                    
                                            
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setModelo(event.carId.modelo);}} placeholder="Modelo" /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setAno(event.target.value);}} placeholder="Ano" name="nome" value={carId.ano} /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setKm(event.target.value);}} placeholder="Km" name="nome" value={carId.km} /> </ul>   
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setDescricao(event.target.value);}} placeholder="Descricao" name="nome" value={carId.descricao} /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setFoto(event.target.value);}} placeholder="Foto" name="nome" value={carId.foto} /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setPreco(event.target.value);}} placeholder="Preco" name="nome" value={carId.preco} /> </ul>  
                                            
                                            
                                            
                                                <ul><Link to="/home">
                                                <button className="btn-edit btn-2" onClick={() => updateCar(carId._id)} type="submit">Send</button>
                                                </Link>
                                                    
                                                    
                                                    </ul>
                                            
                                            
                                            
                                    
                                    
                                    </form>

                            
                            </div>

                        </div>
                    </>
                )
        }

const Create = () => {
            return(
                    <>
                        <Navbar/>
                        <div class="container">
                        
                        <h1>create</h1>
                        
                            <div class="form_box">
                                

                                    
                                    <form action="http://localhost:3000/" method="put">
                                    
                                            
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setModelo(event.target.value);}} placeholder="Modelo" name="nome" /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setAno(event.target.value);}} placeholder="Ano" name="nome" /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setKm(event.target.value);}} placeholder="Km" name="nome" /> </ul>   
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setDescricao(event.target.value);}} placeholder="Descricao" name="nome" /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setFoto(event.target.value);}} placeholder="Foto" name="nome" /> </ul>  
                                            <ul><input className="inputcar" type="text" onChange={(event) => {setPreco(event.target.value);}} placeholder="Preco" name="nome" /> </ul>  
                                            
                                            
                                            
                                                <ul><Link to="/home">
                                                <button className="btn-edit btn-2" onClick={addCar} type="submit">Send</button>
                                                </Link>
                                                    
                                                    
                                                    </ul>
                                            
                                            
                                            
                                    
                                    
                                    </form>

                            
                            </div>

                        </div>
                    </>
                )
 }


   return(
        <>
         {window.location.href === "http://localhost:3000/newcar" ? <Create/> : <Update/> }
        </>

   )



       


   

   

}


export default Newcar ;
